    public static int getResult(String s, String t) {
        if (s.length() % t.length() != 0 || !s.contains(t)) {
            return -1;
        }

        String smallestCommonDivisor = "";

        for (int i = 1; i <= t.length(); i++) {
            String pattern = t.substring(0, i);

            String remainder1 = s.replace(pattern, "");
            String remainder2 = t.replace(pattern, "");

            if (remainder1.length() == 0 && remainder2. length() == 0) {
                smallestCommonDivisor = pattern;
                break;
            }
        }

        return smallestCommonDivisor.length();
    }
    public static int findSmallestDivisor(String planConfig, String displayStr) {
        // if (planConfig.length() % displayStr.length() != 0
        //     || !planConfig.contains(displayStr)) {
        //     return -1;
        // }


        String big = planConfig;
        String small = displayStr;

        if (planConfig.length() > displayStr.length()) {
            big = planConfig;
            small = displayStr;
        } else {
            big = displayStr;
            small =planConfig;
        }

        if (big.length() % small.length() != 0 || !big.contains(small)) {
            return -1;
        }


        String smallestCommonDivisor = "";

        for (int i = 1; i <= small.length(); i++) {
            String pattern = small.substring(0, i);

            String remainder1 = big.replace(pattern, "");
            String remainder2 = small.replace(pattern, "");
//            System.out.println("pattern: "+ pattern);
//            System.out.println("remainder1: "+ remainder1);
//            System.out.println("remainder2: "+ remainder2);
            if (remainder1.length() == 0 && remainder2.length() == 0) {
                smallestCommonDivisor = pattern;
                break;
            }
        }

        return smallestCommonDivisor.length();
    }
